package quicksort_recursion_limits;

import cs_1c.*;
import java.util.*;
import java.text.*;

//------------------------------------------------------
public class Foothill
{
   // -------  main --------------
   public static void main(String[] args) throws Exception
   {
      final int ARRAY_SIZE = 800000;

      int k, randomInt;
      long startTime, stopTime;
      double elapsedTime;
      double averageElapsedTime = 0;
      int fastestRecursionLimit = 0;
      int longestRecursionLimit = 2;
      double currentElapsedTime = 0;
      double fastestElapsedTime = 0;
      double longestElapsedTime = 0;

      // For formatting output neatly
      NumberFormat tidy = NumberFormat.getInstance(Locale.US);
      tidy.setMaximumFractionDigits(4);

      Integer[] mainArrayOfInts = new Integer[ARRAY_SIZE];
      Integer[] referenceOfMainArray = new Integer[ARRAY_SIZE];

      ArrayList<Double> printArray = new ArrayList<>();
      ArrayList<Integer> recursionLimit = new ArrayList<>();

      // Build random integers for the array as the main reference
      for (k = 0; k < ARRAY_SIZE; k++)
      {
         randomInt = (int) (Math.random() * ARRAY_SIZE);
         mainArrayOfInts[k] = randomInt;
      }

      // Find the optimal recursion limit
      for(int a = 2; a <= 300; a += 2) {

         FHsort.setRecursionLimit(a);

         // Create a reference of the same random integers
         // for different recursion limits
         for(int c = 0; c < referenceOfMainArray.length; c++) {
            referenceOfMainArray[c] = mainArrayOfInts[c];
         }

         // Start the quick-sort to the reference array
         startTime = System.nanoTime();  // ------------------ start
         FHsort.quickSort(referenceOfMainArray);
         stopTime = System.nanoTime();    // ---------------------- stop

         elapsedTime = (stopTime - startTime) / 1e9;

         // Make sure there's no duplicate elapsed time
         if(currentElapsedTime != elapsedTime) {
            currentElapsedTime = elapsedTime;
            printArray.add(elapsedTime);
            recursionLimit.add(a);
            System.out.println("quickSort Elapsed Time: "
                  + tidy.format(elapsedTime)
                  + " seconds for recursion limit of : " + a);
         }

         // Initialize the fastest time as the first elapsed time
         if(a == 2) {
            fastestElapsedTime = elapsedTime;
            longestElapsedTime = elapsedTime;
         }

         // Find the fastest elapsed time for different recursion limit
         if(elapsedTime < fastestElapsedTime) {
            fastestElapsedTime = elapsedTime;
            fastestRecursionLimit = a;
         }

         // Find the longest elapsed time for different recursion limit
         if(elapsedTime > longestElapsedTime) {
            longestElapsedTime = elapsedTime;
            longestRecursionLimit = a;
         }

      }

      System.out.println("\n\nArray Size of " + ARRAY_SIZE
            + ", elapsed time measured in seconds.");

      // Printing table starts here------------------------------------------
      for(int i = 0; i < ((printArray.size() * 10) + 15); i++) {
         System.out.print("=");
      }
      System.out.println();

      // Print out the recursion limit
      ListIterator arrayIteration = printArray.listIterator();
      ListIterator limitIteration = recursionLimit.listIterator();

      System.out.print("Recursion Limit");
      // Print out the elapsed time
      while(limitIteration.hasNext()) {
         System.out.print(String.format("%10s", limitIteration.next()));
      }

      System.out.print("\nElapsed Time   ");
      while(arrayIteration.hasNext()) {
         System.out.print(String.format("%10.6s", arrayIteration.next()));
      }
      System.out.println();

      for(int i = 0; i < ((printArray.size() * 10) + 15); i++) {
         System.out.print("=");
      }
      System.out.println();

      // Print out the fastest elapsed time
      System.out.print("The Fastest elapsed time is "
            + tidy.format(fastestElapsedTime)
            + " for recursion limit of " + fastestRecursionLimit + "\n");

      // Print out the longest elapsed time
      System.out.print("The Longest elapsed time is "
            + tidy.format(longestElapsedTime)
            + " for recursion limit of " + longestRecursionLimit + "\n");

      // Print out the average elapsed time
      arrayIteration = printArray.listIterator();
      while(arrayIteration.hasNext()) {
         averageElapsedTime += (Double)arrayIteration.next();
      }
      averageElapsedTime /= printArray.size();
      System.out.println("The Average elapsed time is "
            + tidy.format(averageElapsedTime) + "\n");
   }
}

